import { makeStyles } from '@material-ui/core';
import React from 'react';
import './../css/fonts.css';
import perfil from './../images/perfil.jpg'
import SeeMore from './SeeMore';

const useStyles = makeStyles(()=>({
    card:{
        background:'red',
        width:'330px',
        height:'160px',
        border:'1px solid #D1C19A',
        borderRadius:'10px'
    },
    cardContent:{
        display:'flex',
        flexDirection:'row',
        "& *":{
            color:'#401817'
        }
    },
    imgCont:{
        width:'90px',
        "& img":{
            width:'100%'
            
        }
    },
    infoCard:{
        display:'flex',
        flexDirection:'column'
    },
    nameCard:{
        fontWeight:'bold'
    },
    lnameCard:{
        fontWeight:'bold'
    },
    professionCard:{
        fontSize:'12px'
    },
    positionCard:{
        fontSize:'12px'
    }
}));

const Card = () => {
  const classes = useStyles();
  return (

        <div className={classes.card}>
            <div className={classes.cardContent}>
                <div className={classes.imgCont}><img src={perfil}/></div>
                <div className={classes.infoCard}>
                    <div className={classes.nameCard}>Andrés Miguel</div>
                    <div className={classes.lnameCard}>Saavedra Sanchez</div>
                    <div className={classes.professionCard}><strong>Carrera: </strong>Ing. Físico</div>
                    <div className={classes.positionCard}><strong>Cargo: </strong> Presidente</div>
                </div>
            </div>
            <div><SeeMore /></div>
        </div>

    
  );
}

export default Card;
