import { makeStyles } from '@material-ui/core';
import React from 'react';

import InputForm from './../components/InputForm';


const useStyles = makeStyles(()=>({
  home:{
    textAlign: 'center',
    margin: '80px auto'
  },
  welcome:{
    fontSize: '70px',
    color: '#5A2120'
  },
  info:{
    width:'35%',
    margin:'10px auto',
    fontSize:'18px',
    color:'#464646',
    "& p":{
      color: '#5A2120'
    }
  },
  inputHome:{
    width:'150px',
    margin:'50px auto',
    display:'flex',
    flexDirection:'column',
    "& button":{
      margin:'30px auto',
      padding:'10px 25px',
      fontSize:'15px',
      color:'white',
      background:'#5A2120',
      border:'none',
      transition: '.3s',
      borderRadius: '5px'
    },
    "& button:hover":{
      background:'#9F4341'
    }

  }

}));

const Home = () => {
  const classes = useStyles();
  return (
    
      <div className={classes.home}>
        <h2 className={classes.welcome}>Bienvenid@</h2>
        <p className={classes.info}>a <strong>Voto Informado</strong> UNI, gracias por visitar nuestra página web. 
          Este portal te brinda información sobre las hojas de vida,
          experiencia laboral y política de las y los candidatos que
          participan en las Elecciones Generales UNI 2021.
        </p>
        <form className={classes.inputHome}>
          <InputForm type="text" label="CÓDIGO" />
          <button>Ingresar</button>
        </form>

      </div>
    
  );
}

export default Home;
